import { createSlice } from "@reduxjs/toolkit";

export const cartSlice = createSlice({
    name: "cart",
    initialState: {
        value: 0,
        totalvalue:0
    },
    reducers: {
        increment: (state) => {
            state.value += 1
        },
        decrement:(state)=>{
            state.value -=1
        },
        addProduct:(state)=>{
            state.totalvalue=state.value
        }
    }

})
export const {increment,decrement,addProduct}=cartSlice.actions

export default cartSlice.reducer
